import React from 'react';
import {useState} from 'react';
import '../App.css';
import { Link } from "react-router-dom";



class Register extends React.Component {
    constructor(props) {
        super(props);
		this.handleNameChange = this.handleNameChange.bind(this);
		this.handleUsernameChange = this.handleUsernameChange.bind(this);
		this.handlePasswordChange = this.handlePasswordChange.bind(this);
		this.handleRegisterAttempt = this.handleRegisterAttempt.bind(this);
		this.state({name:"", email:"", username:"", password:""});
    }
	handleNameChange(e){
		e.preventDefault();
		this.setState({name: e.target.value});
	}
	handleEmailChange(e){
		e.preventDefault();
		this.setState({email: e.target.value});
	}
	handleUsernameChange(e){
        e.preventDefault();
        this.setState({username: e.target.value});
        //console.log(this.state.username);
    }
    handlePasswordChange(e){
        e.preventDefault();
        this.setState({password: e.target.value});
        //console.log(this.state.password);
    }

    handleRegisterAttempt(e){//logs the form
        e.preventDefault();
        this.setState({loggedIn: true});
    }

    render() {
        return(//obviously needs more added
            <header className="App-header">
                {true && 
					<div>
						<img src="images/Nooth_DnD.png" width='300' alt="logo"/>
						<h1 className="title">
							Sign Up
						</h1>
						<form className="Form">
							<label htmlFor="fullname" className="tbLabel">Full Name: 
							<input type="name" id="fullname" name="fullname" onChange={this.handleNameChange} value={this.state.name}/></label><br/>
							<label htmlFor="email" className="tbLabel" name="tbLabel">Email: 
							<input type="email" id="email" name="email" onChange={this.handleEmailChange} value={this.state.email}/></label><br/>
							<label htmlFor="username" className="tbLabel" name="tbLabel">Username: 
							<input type="username" id="username" name="username" onChange={this.handleUsernameChange} value={this.state.username}/></label><br/>
							<label htmlFor="password" className="tbLabel" name="tbLabel">Password: 
							<input type="password" id="password" name="password" onChange={this.handlePasswordChange} value={this.state.password}/></label><br/>
						</form><br/>
						<button name="submit">
							Submit
						</button><br/>
						<button name="next" variant="outlined">
							<Link to="/SomePage">
								Go to some page
							</Link>
						</button>
					</div>
				}
            </header>
            )
    }
}

export default Register;
