import React, { useContext } from 'react';
import {useState} from 'react';
import '../App.css';
import { Link } from "react-router-dom";
import {UserContext} from '../components/Main';

function Campaigns() {
	
	const me = useContext(UserContext);
    const [selected, changeSelected] = useState(null)
    console.log(me.campaigns)
	return(
		<header className="App-header">
            {!selected && <DisplayCampaigns me={me} changeSelected={changeSelected}/>}
            {selected && <EditCampaign campaign={selected}/>}
		</header>
	)
}

function DisplayCampaigns(me){
    return (
        <div>
            <img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
            <h1 className="title">
                Campaigns
            </h1>
            <p>These are your campaigns: {JSON.stringify(me.campaigns)}</p>
        </div>
    )
}

function Campaign(campaign){
    return (
        <div>
            <ul className="campaign">
                <ul className="col1">
                    <p>{campaign.name}</p>
                </ul>
                <ul className="col2">
                    <button>

                    </button>
                </ul>
            </ul>
        </div>
    )
}

function EditCampaign(campaign) {
	
	const me = useContext(UserContext);
    console.log(campaign)
	return(
		<header className="App-header">
			<img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
			<h1 className="title">
				Campaigns
			</h1>
            <p>These are your campaigns: {JSON.stringify(me.campaigns)}</p>
		</header>
	)
}

export default Campaigns;
