import React, {useState, useEffect} from 'react';
import './App.css';
import {useQuery} from '@apollo/client';
import {getUser} from './queries';
import Main from './components/Main.js';
import Login from './components/Login';

class App extends React.Component {
    constructor(props) {
        super(props);
		this.LoginUser = this.LoginUser.bind(this);
		this.state = {username:"", password:"", isLoggedIn: false};
    }

	LoginUser(username, password){
		this.setState({username: username, password: password, isLoggedIn: true})
		console.log(this.state.username, this.state.password);
	}

    render() {

        function useCurrentUser(username, password){
            const [me, getMe] = useState(null);
            const currentUser = useQuery(getUser, {variables:{username:this.state.username, password:this.state.password}}).data;
            useEffect(()=>{//no actual dependencies as currentUser is already defined
                getMe(currentUser);
            });
            console.log(me);
        }

        return (
            <div className="App">
                {!this.state.isLoggedIn && <Login handleLogin={this.LoginUser}/>}
                {this.state.isLoggedIn && <Main />}
            </div>
        );
    }
}
export default App;
