import React from 'react';
import {useState} from 'react';
import '../App.css';
//import apollo client and its relevant queries
import {useQuery} from "@apollo/client";
import {client, getUsers} from "../index"

function Users() {
    
    const {userData} = useQuery(getUsers);
	const [users, fetchUsers] = useState(userData);

	return(
		<body>
			<p>
				{users}
			</p>
			<button name="showusers" onClick={() => fetchUsers("click the button to fetch users")}>
				get users
			</button>
		</body>
		)
}

export default Users;
