import React from 'react';
import '../App.css';
import {useQuery} from '@apollo/client';
import {getUser} from '../queries';



function Login() {
	var givenUsername = '';
	var givenPassword = '';
	var usersname = '';

	const getuser = useQuery(getUser, {
		variables: givenUsername
	});

	function checkLogin(username, password) {
		givenUsername = username;
		if (getuser != null){
			checkPassword(password);
		}
	}
	
	function checkPassword(password) {
		givenPassword = password;
	}

	return (
		<div><br/><br/>
			<p>
				Please enter your username and password
			</p>
			<form id="Form">
				<label htmlFor="user">Username: </label>
				<input
					type="text"
					name="user"
					required={true}
					autoFocus={true}
				></input><br />
				<label htmlFor="pass">Password: </label>
				<input
					type="password"
					name="pass"
					required={true}
				></input><br />
				<button /*onClick="checkUsername(user.value), checkPassword(pass.value)"*/ variant="outlined">login</button>
				<input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />
				<label htmlFor="rememberLogin"> Remember me</label>
			</form>
		<br/><br/></div>
	)
}

export default Login;
