import React, { useEffect } from 'react';
import {useQuery} from '@apollo/client';
import {UserContext} from '../misc/UserContext'
import {getUser} from '../queries';
import '../App.css';
import PageSwitch from './PageSwitch'


export default function Main(props){

	//console.log(props.username, props.password);
	const {loading, data, error, refetch} = useQuery(getUser, {variables:{username: props.username, password: props.password}});
	console.log("Logged in user", data);
	
	while(loading){
		return (<p>loading...</p>);
	}
	while(error){
		return (<p>There was an error. Please try again.</p>);
	}
	const context = { user:data.user, refreshUser:refetch }
	if(data != null){
		return (
			<UserContext.Provider value={context}>
				<PageSwitch reloadUser={()=>refetch()}/>
			</UserContext.Provider>
		);
	}else{
		props.BadLogin();
		return (<p>Error: User not found</p>);
	}
}
