/**
 * This is the file that handles routing for the entire application
 */
import React from 'react';
import { Switch, Route } from 'react-router-dom';
import {useQuery} from '@apollo/client';
import {getUser, getUserByID} from '../queries';
import '../App.css';
import Home from '../pages/Home';
import SomePage from '../pages/SomePage';
import SomeOtherPage from '../pages/SomeOtherPage';
import Register from '../pages/Register';
import Campaigns from '../pages/Campaigns';

export const UserContext = React.createContext(null);

function Main(props){

	console.log(props.username, props.password);
	if (props.id===""){
		const {loading, data, error} = useQuery(getUser, {variables:{username: props.username, password: props.password}});
	}else{
		const {loading, data, error} = useQuery(getUserByID, {variables:{username: props.username, password: props.password}});
	}
	console.log(data);//always undefined
	
	while(loading){
		return (<p>loading...</p>);
	}
	while(error){
		return (<p>There was an error. Please try again.</p>);
	}
	while(data){
		if(data.user != null){
			props.GoodLogin(data.user.id)
			return (
				<UserContext.Provider value={data.user}>
					<Switch> {/* The Switch decides which component to show based on the current URL.*/}
						<Route exact path='/' component={Home}/>
						<Route exact path='/SomePage' component={SomePage}/>
						<Route exact path='/SomeOtherPage' component={SomeOtherPage}/>
						<Route exact path='/Register' component={Register}/>
						<Route exact path='/Campaigns' component={Campaigns}/>
					</Switch>
				</UserContext.Provider>
			);
		}else{
			props.BadLogin();
			return (<p>Error: User not found</p>);
		}
	}
}

export default Main;
