    }

    render() {
        if(this.campaignID != null){
            return(//also need to add characters/users +/-
                <div>
                    {this.state.valid && <div><img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
                    <h1 className="title">
                        Edit Campaign
                    </h1>
                    <form className="Form" onSubmit={this.submit}>
                        <label htmlFor="name" className="tbLabel">Campaign Name: 
                        <input type="name" id="name" name="name" required={true} onChange={this.handleNameChange} value={this.state.name}/></label><br/>
                        <input type="submit" value="Submit"/>
                    </form><br/>
                    <button onClick={this.props.back}>Go Back</button></div>}
                </div>
            )
        }else{
            return (
                <div>
                    <p>couldn't find campaign</p>
                    <button onClick={this.props.back}>Go Back</button>
                </div>
            )
        }
    }
}

function LoadCampaign(props){
    const {data, loading, error} = useLazyQuery(getCampaign, {variables:{id:props.campaign}})
    while(loading){
        return(<p>loading...</p>)
    }
    if(error){
        return(<p>{error}</p>)
    }else{
        console.log("selected campaign", data)
        return(<p>done</p>)
    }
}
