import React, {useState, useEffect} from 'react';
import './App.css';
import Main from './components/Main.js';
import Login from './components/Login';

class App extends React.Component {
    constructor(props) {
        super(props);
		this.LoginUser = this.LoginUser.bind(this);
        this.InvalidLogin = this.InvalidLogin.bind(this);
		this.state = {username:"", password:"", isLoggedIn: false};
    }

	LoginUser(username, password){
		this.setState({username: username, password: password}, ()=>{console.log(this.state.username, this.state.password);this.SetLoggedIn();});
        
		//saves the last subission as the current state for some reason, not current submit
	}

    SetLoggedIn(){
        if(!!this.state.username && !!this.state.password){
            this.setState({isLoggedIn: true}, ()=>{console.log("logged in")});
        }
    }

    InvalidLogin(){//maybe want to look into a "validUser" bool instead of an option for good and one for bad
        this.setState({isLoggedIn: false});
    }

    render() {

        return (
            <div className="App">
                {!this.state.isLoggedIn && <Login handleLogin={this.LoginUser}/>}
                {this.state.isLoggedIn && <Main username={this.state.username} password={this.state.password} BadLogin={this.InvalidLogin}/>}
            </div>
        );
    }
}
export default App;
