import React, { useContext } from 'react';
import '../App.css';
import { UserContext } from '../components/Main';

export default class Campaigns extends React.Component {
    constructor(props) {
        super(props)
        this.changeSelected = this.changeSelected.bind(this)
        this.clearSelected = this.clearSelected.bind(this)
        this.state = {selected:null, chosen:false}
    }

    changeSelected(selected){
        this.setState({selected:selected, chosen:true})
    }

    clearSelected(){
        this.setState({selected:null, chosen:false})
    }

    render() {
        //const me = this.context
        //console.log(me)
        return(
            <header className="App-header">
                {this.state.chosen===false && <DisplayCampaigns me={"me"} changeSelected={this.changeSelected}/>}
                {this.state.chosen===true && <EditCampaign campaign={this.state.selected} back={this.clearSelected}/>}
            </header>
        )
    }
}

function DisplayCampaigns(props){//need to render the Campaign function for as many as there are in campaigns collection
    const currentUser = useContext(UserContext)
    //console.log(currentUser)
    const campaignList = []
    for (let i = 0; i<currentUser.campaigns.length; i++){//adds jsx elemnts to the array
        campaignList.push(<Campaign campaign={currentUser.campaigns[i]} changeSelected={props.changeSelected}/>)
        //console.log(currentUser.campaigns[i])
    }
    return (
        <div>
            <img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
            <h1 className="title"> Campaigns </h1>
            <p>These are your campaigns:</p>
            {campaignList}
        </div>
    )
}

function Campaign(props){
    return (
        <div>
            <ul className="campaign">
                <ul className="col1">
                    <p>{props.campaign.name}</p>
                </ul>
                <ul className="col2">
                    <button onClick={() => props.changeSelected(props.campaign)}>
                        Edit
                    </button>
                </ul>
            </ul>
        </div>
    )
}

function EditCampaign(props) {
	const campaign = props.campaign
    console.log(campaign)
	if(campaign != null){
        return(
            <div>
                <img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
                <h1 className="title">
                    Edit Campaign
                </h1>
                <form className="Form" onSubmit={this.handleRegisterAttempt}>
                    <label htmlFor="name" className="tbLabel">Campaign Name: 
                    <input type="name" id="name" name="name" required={true} onChange={this.handleNameChange} value={this.state.name}/></label><br/>
                    <label htmlFor="email" className="tbLabel" name="tbLabel">Email: 
                    <input type="email" id="email" name="email" required={true} onChange={this.handleEmailChange} value={this.state.email}/></label><br/>
                    <label htmlFor="username" className="tbLabel" name="tbLabel">Username: 
                    <input type="username" id="username" name="username" required={true} onChange={this.handleUsernameChange} value={this.state.username}/></label><br/>
                    <label htmlFor="password" className="tbLabel" name="tbLabel">Password: 
                    <input type="password" id="password" name="password" required={true} onChange={this.handlePasswordChange} value={this.state.password}/></label><br/>
                    <input type="submit" value="Submit"/>
                </form><br/>
            </div>
        )
    }else{
        return (
            <div>
                <p>couldn't find campaign</p>
                <button onClick={props.back}>Go Back</button>
            </div>
        )
    }
}
