import React, { useEffect } from 'react';
import '../App.css';
import {useState} from 'react';
import {useQuery} from '@apollo/client';
import {getUser} from '../queries';



function Login() {
	var givenUsername = '';
	var usersname = '';

	const getuser = useQuery(getUser, {variables: givenUsername}).data;
	var [user, greetuser] = useState('');

	function checkLogin(username, password) {
		givenUsername = username;
		if (getuser){
			if (getuser.password === password){
				usersname = getuser.name;
				greetuser("Hello, " + usersname);
			}else{
				greetuser("incorrect password");
			}
		}else{
			greetuser("unrecognised username");
		}
	}

	return (
		<div><br/><br/>
			<p>
				Please enter your username and password
			</p>
			<form id="Form">
				<label htmlFor="user">Username: </label>
				<input
					type="text"
					name="user"
					required={true}
					autoFocus={true}
				></input><br />
				<label htmlFor="pass">Password: </label>
				<input
					type="password"
					name="pass"
					required={true}
				></input><br />
				/*<button onClick={checkLogin("user.value", "pass.value")} variant="outlined">login</button>
				<p>{user}</p>*/
				<input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />
				<label htmlFor="rememberLogin"> Remember me</label>
			</form>
		<br/><br/></div>
	)
}

export default Login;
