import React, { useContext } from 'react';
import {useState} from 'react';
import '../App.css';
import { Link } from "react-router-dom";
import {UserContext} from '../components/Main';

class Campaigns extends React.Component {
    static contextType = UserContext
    constructor(props) {
        super(props);
        this.me = this.context;
        this.changeSelected = this.changeSelected.bind(this)
        this.setState({selected:null})
    }

    changeSelected(selected){
        this.setState({selected:selected})
    }

    clearSelected(){
        this.setState({selected:null})
    }

    render() {
        return(
            <header className="App-header">
                {!this.state.selected && <DisplayCampaigns me={this.me} changeSelected={this.changeSelected}/>}
                {this.state.selected && <EditCampaign campaign={this.state.selected}/>}
            </header>
        )
    }
}

function DisplayCampaigns(props){//need to render the Campaign function for as many as there are in campaigns collection
    console.log(props.me.campaigns)
    const campaignList = []
    for (const campaign in props.me.campaigns){//adds jsx elemnts to the array
        campaignList.push(<Campaign campaign={campaign} changeSelected={props.changeSelected}/>)
    }
    return (
        <div>
            <img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
            <h1 className="title">
                Campaigns
            </h1>
            <p>These are your campaigns: {JSON.stringify(props.me.campaigns)}</p>
            {campaignList}
        </div>
    )
}

function Campaign(props){
    return (
        <div>
            <ul className="campaign">
                <ul className="col1">
                    <p>{props.campaign.name}</p>
                </ul>
                <ul className="col2">
                    <button onClick={props.changeSelected(props.campaign._id)}>
                        Edit
                    </button>
                </ul>
            </ul>
        </div>
    )
}

function EditCampaign(campaign) {
	
	const me = useContext(UserContext);
    console.log(campaign)
	return(
		<header className="App-header">
			<img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
			<h1 className="title">
				Campaigns
			</h1>
            <p>These are your campaigns: {JSON.stringify(me.campaigns)}</p>
		</header>
	)
}

export default Campaigns;
