import React from 'react';
import '../App.css';
import { useQuery } from '@apollo/client';
import {getUser} from "../queries"

function GreetUser(props){//retrieves the user based on username and password and returns a greeting
	console.log(props)
    const {called, loading, data} = useQuery(getUser, {username: props.username});//do NOT attempt "loaduser();" with no trigger
	console.log(data);
	while(called && loading){
		//loaduser(getUser, {username: props.username});
		return( <p>Loading...</p> )
	}
	if (data.user){
        return( <p>Hello, {data.user.name}! </p> )
    }else{
        return( <p>user not found</p> )
    }
}

export default GreetUser;
