import React from 'react';
import {useState} from 'react';
import '../App.css';
import { Link } from "react-router-dom";
import Login from "../components/Login";

import {getUsers} from "../index"

function Home() {

	const [buttonText, setButtonText] = useState("Next");
	const [users, fetchUsers] = useState("click the button to get the users list");

	return(
		<header className="App-header">
			<img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
			<h1 className="title">
				DnD20
			</h1>
			<p>
				{users}
			</p>
			<button name="showusers" onClick={() => fetchUsers(getUsers())}>
				get users
			</button>
			<Login />
			
			<button name="showpong" onClick={() => setButtonText("baba")}>
				{buttonText}
			</button>
			<button name="next" variant="outlined">
				<Link to="/SomePage">
					{buttonText}
				</Link>
			</button>
			<button name="next2" variant="outlined">
				<Link to="Register">
					Go to registration
				</Link>
			</button>
		</header>
		)
}

export default Home;
