import React, { useEffect } from 'react';
import '../App.css';
import {useLazyQuery} from '@apollo/client'
import {getUser} from '../queries'
//import LoginForm from './loginForm'

class Login extends React.Component {
    
    constructor(props) {
        super(props);
        this.handleLoginAttempt = this.handleLoginAttempt.bind(this);
        this.state = {loggedIn: false, username: "", password: ""};
    }

    handleUsernameChange(e){
        e.preventDefault();
        const user = e.target.value;
        this.setState({username: user});
        console.log(this.state.username);
    }
    handlePasswordChange(e){
        e.preventDefault();
        const pass = e.target.value.pass;
        this.setState({password: pass});
        console.log(this.state.password);
    }

    handleLoginAttempt(e){//doesnt do anything, but does log a form
        //un = e.target.username;
        //pw = e.target.password;
        console.log(e.target)
        e.preventDefault();
        //this.setState({loggedIn: true});
    }

    render() {
        return (
            <div><br/><br/>
                {!(this.loggedIn) && //show login form if not logged in
                    (<form id="Form" onSubmit={this.handleLoginAttempt}>
                        <p> Please enter your username and password </p>
                        <label htmlFor="user">Username: 
                            <input type="username" name="user" id="user" onChange={this.handleUsernameChange} value={this.state.username} required={true} autoFocus={true}/>
                        </label><br />
                        <label htmlFor="pass">Password: 
                            <input type="password" name="pass" id="pass" onChange={this.handlePasswordChange} value={this.state.password} required={true}/>
                        </label><br />
                        <input type="submit" value="Submit" variant="outlined"/>
                        <label htmlFor="rememberLogin"> Remember me
                            <input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />{/*not implemented yet*/}
                        </label>
                    </form>)
                }
                {this.loggedIn && <GreetUser username={this.state.username} password={this.state.password}/>}
            <br/><br/></div>
        )
    }
}

function GreetUser(props){//retrieves the user based on username and password and returns a greeting
    const [loaduser, {called, loading, data}] = useLazyQuery(getUser, {username: this.props.username});
	console.log(data);
	while(called && loading){
		return( <p>Loading...</p> )
	}
	if (data){
        return( <p>Hello, {data.name}! </p> )
    }else{
        return( <p>user not found</p> )
    }
}

export default Login;
