import React, { useEffect } from 'react';
import '../App.css';
import {useLazyQuery} from '@apollo/client'
import {getUser} from '../queries'


class Login extends React.Component {
    
    constructor(props) {
        super(props);
        this.loggedIn = false;
        this.username = '';
        this.password = '';
        this.handleLoginAttempt = this.handleLoginAttempt.bind(this);
        this.state = {loggedIn: false, username: this.username, password: this.password};
    }

    handleLoginAttempt(e){
        console.log(e)
        //this.setState(this.username = e.username, this.password = e.password, this.loggedIn = true);
    }

    render() {
        return (
            <div><br/><br/>
                {!(this.loggedIn) && <LoginForm username={this.state.username} password={this.state.password} handleLogin={this.handleLoginAttempt}/>}
                {this.loggedIn && <GreetUser username={this.state.username} password={this.state.password}/>}
            <br/><br/></div>
        )
    }
}

function LoginForm(props){//form to enter username and password
    return (
        <form id="Form" onSubmit={this.props.handleLogin}>
            <p> Please enter your username and password </p>
            <label htmlFor="user">Username: 
                <input type="username" name="user" id="user" required={true} autoFocus={true}/>
            </label><br />
            <label htmlFor="pass">Password: 
                <input type="password" name="pass" id="pass" required={true}/>
            </label><br />
            <button onClick="Submit" variant="outlined">login</button>
            <label htmlFor="rememberLogin"> Remember me
                <input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />{/*not implemented yet*/}
            </label>
        </form>
    )
}

function GreetUser(props){//retrieves the user based on username and password and returns a greeting
    const [loaduser, {called, loading, data}] = useLazyQuery(getUser, {username: this.props.username});
	console.log(data);
	while(called && loading){
		return( <p>Loading...</p> )
	}
	if (data){
        return( <p>Hello, {data.name}! </p> )
    }else{
        return( <p>user not found</p> )
    }
}

export default Login;
