import React from 'react';
import {useState} from 'react';
import '../App.css';
//import apollo client and its relevant queries
import {useQuery} from "@apollo/client";
import {Users as getusers} from "../index"

function Users() {
    
    const {userData} = useQuery(getusers);
	const [users, fetchUsers] = useState("click the button to fetch users");

	return(
		<body>
			<p>
				{users}
			</p>
			<button name="showusers" onClick={() => fetchUsers(userdata)}>
				get users
			</button>
		</body>
	)
}

export default Users;
