import React from 'react';
import '../App.css';
import { useLazyQuery } from '@apollo/client';
import {getUser} from "../queries"

async function GreetUser(props){//retrieves the user based on username and password and returns a greeting
    const [loaduser, {called, loading, data}] = useLazyQuery(getUser, {username: this.props.username});//do NOT attempt loaduser(); with no trigger
	console.log(data);
	while(called && loading){
		loaduser(getUser, {username: props.username});
		return( <p>Loading...</p> )
	}
	if (data){
        return( <p>Hello, {data.name}! </p> )
    }else{
        return( <p>user not found</p> )
    }
}

export default GreetUser;
