import React from 'react';
import '../App.css';
import {useLazyQuery} from '@apollo/client'
import {getCampaign, renameCampaign} from '../queries'

export default class EditCampaign extends React.Component {
    constructor(props) {
        super(props);
        this.campaignID = this.props.campaignID
        console.log(this.campaignID)
        this.handleNameChange = this.handleNameChange.bind(this)
        this.submit = this.submit.bind(this)
        this.returnCampaign = this.returnCampaign.bind(this)
        this.state = {loaded:false, campaign:null, name:""}
    }

    handleNameChange(e){
		e.preventDefault();
		this.setState({name: e.target.value});
	}

    submit(e){
        this.props.submit(e)
    }

    returnCampaign(campaign){
        this.setState({campaign:campaign, loaded:true})
    }

    render() {
        if(this.campaignID != null){
            return(//also need to add characters/users +/-
                <div>
                    {!this.state.loaded && <div>
                        <LoadCampaign ID={this.campaignID} return={this.returnCampaign}/>
                    </div>}
                    {this.state.loaded && <div><img src="images/Nooth_DnD.png" className="App-logo" alt="logo" />
                    <h1 className="title">
                        Edit Campaign
                    </h1>
                    <form className="Form" onSubmit={this.submit}>
                        <label htmlFor="name" className="tbLabel">Campaign Name: 
                        <input type="name" id="name" name="name" required={true} onChange={this.handleNameChange} value={this.state.name}/></label><br/>
                        <input type="submit" value="Submit"/>
                    </form><br/></div>}
                    <button onClick={this.props.back}>Go Back</button>
                </div>
            )
        }else{
            return (
                <div>
                    <p>couldn't find campaign</p>
                    <button onClick={this.props.back}>Go Back</button>
                </div>
            )
        }
    }
}

function LoadCampaign(props){
    const {data, loading, error} = useLazyQuery(getCampaign, {variables:{id:props.ID}})
    while(loading){
        return(<p>loading...</p>)
    }
    if(error){
        return(<p>{error}</p>)
    }else{
        console.log("selected campaign", data)
        return(<p>done</p>)
    }
}
