import React, { useEffect } from 'react'

export default class LoginForm extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {

        //form to enter username and password
        return (
            <form id="Form" onSubmit={this.props.handleLogin}>
                <p> Please enter your username and password </p>
                <label htmlFor="user">Username: 
                    <input type="username" name="user" id="user" required={true} autoFocus={true}/>
                </label><br />
                <label htmlFor="pass">Password: 
                    <input type="password" name="pass" id="pass" required={true}/>
                </label><br />
                <input type="submit" variant="outlined"/>
                <label htmlFor="rememberLogin"> Remember me
                    <input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />{/*not implemented yet*/}
                </label>
            </form>
        )
    }
}
