import React, { useEffect } from 'react';
import '../App.css';
import {useState} from 'react';
import {useLazyQuery} from '@apollo/client';
import {getUser} from '../queries';
import User from './User';



function Login() {
	var givenUsername = '';
	var usersname = '';

	const handleLogin = (e) => {
		e.preventDefault();
		const formData = new FormData(e.target);
		usersname = useLazyQuery(getUser, {username: formData.user}).data.name;
	}

	return (
		<div><br/><br/>
			<p>
				Please enter your username and password
			</p>
			<form id="Form">
				<label htmlFor="user">Username: 
				<input
					type="text"
					name="user"
					required={true}
					autoFocus={true}
				></input></label><br />
				<label htmlFor="pass">Password:
				<input
					type="password"
					name="pass"
					required={true}
				></input> </label><br />
				<button onClick={checkLogin(user, pass)} variant="outlined">login</button>
				<label htmlFor="rememberLogin"><input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />
				 Remember me</label>
			</form>
			<User/>
		<br/><br/></div>
	)
}

export default Login;
