import React from 'react';
import '../App.css';
import {useLazyQuery} from '@apollo/client'
import {getUser} from '../queries'

function LoginForm(props){
    
	function handleSubmit(e){//need callback in Login to pass this data back up
		const formData = new FormData(e.target);
		const username = formData.get("user");
		const password = formData.get("pass");
        e.preventDefault();
	}

    return (
    <form id="Form">
    <label htmlFor="user">Username: </label>
    <input
        type="username"
        name="user"
        required={true}
        autoFocus={true}
    ></input><br />
    <label htmlFor="pass">Password: </label>
    <input
        type="password"
        name="pass"
        required={true}
    ></input><br />
    <button onClick={handleSubmit} variant="outlined">login</button>
    <input type="checkbox" id="rememberLogin" name="rememberLogin" value="True" />
    <label htmlFor="rememberLogin"> Remember me</label>
    </form>
    )
}

function GreetUser(props){
    const [loaduser, {called, loading, data}] = useLazyQuery(getUser, {username: props.username});
	console.log(data);
	if(called && loading){
		return <p>Loading...</p>
	}
	if (data){
        return(
            <p>
                {data.name}
            </p>
        )
    }else{
        return( <p>"user not found"</p> )
    }
}

class Login extends React.Component {
    

    constructor(props) {
        super(props);
        this.state.loggedIn = false;
        this.givenUsername = '';
        this.givenPassword = '';
		this.handleSubmit = this.handleSubmit.bind(this);
    }


    render() {
		
        return (
            <div><br/><br/>
                <p>
                    Please enter your username and password
                </p>
                {this.loggedIn && <LoginForm/>}
                {this.loggedIn && <GreetUser/>}
            <br/><br/></div>
        )
    }
}

export default Login;
